$(document).ready(function(){
    /*  Activate the tooltips      */
    $('[rel="tooltip"]').tooltip();

    $('.wizard-card').bootstrapWizard({
        'tabClass': 'nav nav-pills',
        'nextSelector': '.btn-next',
        'previousSelector': '.btn-previous',

         onInit : function(tab, navigation, index){

           //check number of tabs and fill the entire row
           var $total = navigation.find('li').length;
           $width = 100/$total;

           $display_width = $(document).width();

           navigation.find('li').css('width',$width + '%');

        },
        onNext: function(tab, navigation, index){
                return validateFifthStep();
        },
        onTabClick : function(tab, navigation, index){
            // Disable the posibility to click on tabs
            return false;
        },
        onTabShow: function(tab, navigation, index) {
            var $total = navigation.find('li').length;
            var $current = index+1;

            var wizard = navigation.closest('.wizard-card');

            // If it's the last tab then hide the last button and show the finish instead
            if($current >= $total) {
                $(wizard).find('.btn-next').hide();
                $(wizard).find('.btn-finish').show();
            } else {
                $(wizard).find('.btn-next').show();
                $(wizard).find('.btn-finish').hide();
            }
        }
    });
//    $('.wizard-card').bootstrapWizard('show', 5);
//    if($('.wizard-card #greenCardAssetsManagement').val() == 'true'){
             $('.wizard-card').bootstrapWizard('show', $("#step").val());
//         }else {
//             $('.wizard-card').bootstrapWizard('show', 1);
//         }
//    $('.wizard-card').bootstrapWizard('show', 2);
});


function validateFifthStep(){

  $("#domesticPersonRegistrationTradingInfo").validate({
      rules: {
           comments:{
               maxlength:255
           }
      },
      submitHandler: function (form) {
          form.submit();
      },
      highlight: function(element) {
          $(element).closest('.form-group').addClass('has-error');
      },
      unhighlight: function(element) {
          $(element).closest('.form-group').removeClass('has-error');
      },
      messages: {
          comments:{
              maxlength:"备注不能超过255位"
          }
      }
  });

  if($("#domesticPersonRegistrationTradingInfo").valid()){
     return true;
  }
  return false;
}

