$(document).ready(function(){
    $.material.init();

    $('body').on('change','select', function (ev){
        if($(this).find('option:selected').val() == ""){
            $(this).css('color','#bdbdbd');
            $(this).children().not(':first').css('color','#555');
        }else{
            $(this).css('color','#555');
            $(this).children().not(':first').css('color','#555');
        }
    });

    $('body select').each(function (ev){
        if($(this).find('option:selected').val() == ""){
            $(this).css('color','#bdbdbd');
            $(this).children().not(':first').css('color','#555');
        }else{
            $(this).css('color','#555');
            $(this).children().not(':first').css('color','#555');
        }
    });
});